import{_ as s}from"./entry.98dfd619.js";import{B as g,_ as a}from"./index.7b78a5c4.js";import{a7 as p,a0 as m,D as n,R as h,a8 as r,P as e,a2 as c}from"./swiper-vue.a421f1cd.js";import"./dropdown.3b7ba1b5.js";import"./index.6d00dcf5.js";import"./index.a68a0920.js";import"./index.42b56407.js";import"./KeyCode.6e31e5e1.js";import"./zoom.cba4073c.js";import"./ResizeObserver.es.0f9f8adb.js";import"./collapseMotion.f1f8d61b.js";import"./index.f15daada.js";const u={},k={class:"mx-auto"},l=r('<section class="px-3 md:px-[50px] w-full"><div class="content-page text-gray-600"><div class="article-content"><div class="article-pages"><h4><span style="font-size:14px;"><strong>Cam kết sản phẩm:</strong></span></h4><h4><span style="font-size:14px;">Mwc.com.vn cam kết bán hàng nguyên mới và đảm bảo đúng chất lượng, sản phẩm mà khách hàng nhận được phải nguyên vẹn và đúng với các thông tin mô tả trên website Mwc.com.vn<br>Nếu Quý khách nhận được sản phẩm không đúng với cam kết trên, bị lỗi kỹ thuật, không vừa size, không ưng ý, Quý khách vui lòng mang sản phẩm đến cửa hàng nội bộ của Mwc.com.vn  hoặc gửi sản phẩm cho Mwc.com.vn trong vòng 07 ngày kể từ thời điểm nhận hàng. Mwc.com.vn xin từ chối hỗ trợ mọi khiếu nại về tình trạng ngoại quan của sản phẩm trong trường hợp Quý khách thông báo cho Mwc.com.vn sau thời gian này.</span></h4><h4><span style="font-size:14px;"><strong>Chính sách đổi hàng:</strong></span></h4><h4><span style="font-size:14px;">Tất cả hàng hóa mua tại website Mwc.com.vn đều được áp dụng chính sách cho phép khách hàng có thể đổi hàng trong vòng 07 ngày trong trường hợp sản phẩm bị trầy xước khi vận chuyển, giao nhầm, giao thiếu, lỗi kỹ thuật, hay không như cam kết trên website và <strong>chỉ đổi 01 lần duy nhất với giá bằng hoặc cao hơn, nếu thấp hơn sẽ không được hoàn tiền</strong>.<br>Mwc.com.vn không áp dụng đổi hàng theo nhu cầu của khách hàng khi sản phẩm không lỗi với các sản phẩm khuyến mại, tại các sự kiện bán hàng khuyến mãi Online, sản phẩm thanh lý,…</span></h4><h4><span style="font-size:14px;"><strong>Điều khoản và điều kiện đổi hàng:</strong></span></h4><h4><span style="font-size:14px;">Vui lòng đọc kỹ <strong>Điều kiện đổi hàng</strong>. Yêu cầu đổi hàng được xem là hợp lệ nếu thỏa mãn đầy đủ các điều kiện sau:<br>- Chưa qua sử dụng, không bị dơ bẩn. (khi thử các sản phẩm giày dép, bạn nên mang vớ và thử giày trên bề mặt sạch)<br>- Sản phẩm được đổi phải được gửi kèm theo <strong>vận đơn được bỏ kèm trong kiện hàng</strong> (còn nguyên vẹn) của mwc.com.vn<br>- Sản phẩm được gửi kèm với đầy đủ phụ kiện (bao gồm cả thẻ bảo hành, vỏ hộp sản phẩm...) và quà tặng kèm (nếu có).<br>- Sản phẩm còn trong thời hạn hiệu lực cho phép đổi hàng (<strong>07 ngày</strong> kể từ thời điểm nhận hàng).</span></h4><h4><span style="font-size:14px;"><strong>Phí đổi hàng:</strong></span></h4><h4><span style="font-size:14px;">Chi phí đổi hàng là hoàn toàn MIỄN PHÍ khi mua sắm tại Mwc.com.vn Tuy nhiên trường hợp Khách hàng muốn đổi sản phẩm vì lí do chủ quan (Không vừa size, không thích màu...), khách hàng vui lòng chịu phí vận chuyển (nếu trong trường hợp cần chuyển hàng cần đổi về kho Mwc.com.vn)</span></h4></div></div></div></section>',1);function v(_,d){const i=p("router-link"),t=a,o=g;return e(),m("div",k,[n(o,{class:"bg-gray-50 md:px-[50px] px-3 py-2"},{default:h(()=>[n(t,null,{default:h(()=>[n(i,{to:"/"},{default:h(()=>[c("Trang chủ")]),_:1})]),_:1}),n(t,null,{default:h(()=>[c("Chính sách đổi trả")]),_:1})]),_:1}),l])}const T=s(u,[["render",v]]);export{T as default};
